import React, { createContext, useState, useContext } from 'react';

// Create the authentication context
const AuthContext = createContext();

// Create a custom hook to access the authentication context in components
export const useAuth = () => useContext(AuthContext);

// AuthProvider component to wrap your app with the authentication context
export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // You can implement your login/logout logic here and update the isLoggedIn state accordingly

  return (
    <AuthContext.Provider value={{ isLoggedIn, setIsLoggedIn }}>
      {children}
    </AuthContext.Provider>
  );
};
