import React, { useState, useEffect } from 'react';
import './style/Navbarstyle.css';
import { useAuth } from './AuthContext';

const NAVBAR = () => {
  const { isLoggedIn, setIsLoggedIn } = useAuth();

  const handleLogout = () => {
    // Perform any logout actions if needed
    setIsLoggedIn(false); // Update isLoggedIn state to false on logout
    window.location.href = '/login'; // Redirect to the login page or perform any other navigation logic
  };
  return (
    <div>
      <div className='containerss' style={{ width: '100%' }}>
        <div className='menu'></div>

        <div className='main-logo'>
          <img
            src="https://play-lh.googleusercontent.com/lvAGNNFIBMC3ZgS-JJcSwDzpSjOGMg7FGY14FrffJobLOed9-H2DiuZwknsIwVwKwTjo"
            className="logo"
            alt=""
            height="50"
            margin-top="2"
          />
          <div className='inshort-text'>
            <div className='text3'> inshorts
              <div className='tagline'>stya informe</div>
            </div>
          </div>
        </div>
        {isLoggedIn ? (
          <div className='login2' onClick={handleLogout}>
            Logout
          </div>
        ) : (
          <div className='login2' onClick={() => (window.location.href = '/login')}>
            Login
          </div>
        )}
      </div>
    </div>
  );
};

export default NAVBAR;
